package main

import "fmt"

func main() {
	ans1 := FirstProblem(getInput())
	fmt.Println("First problem answer:", ans1)

	ans2 := SecondProblem(getInput())
	fmt.Println("Second problem answer:", ans2)
}

// Answer: 280
func FirstProblem(input string) int {
	count := 0
	for _, c := range input {
		if c == '(' {
			count++
		} else if c == ')' {
			count--
		}
	}
	return count
}

// Ans: 1797
func SecondProblem(input string) int {
	count := 0
	var i int
	for _, c := range input {
		if c == '(' {
			count++
		} else if c == ')' {
			count--
		}
		if count == -1 {
			break
		}
		i++
	}
	return i+1
}


